#include <regex>

std::string markdown_to_html(const std::string& markdown);

std::string markdown_to_html(const std::string& markdown) {
    std::string html;
    int level = 1; // Heading level

    // Headers
    std::string headerPattern = "# (.*)";
    std::regex headerRegex(headerPattern);
    std::smatch headerMatch;
    while (std::regex_search(markdown, headerMatch, headerRegex)) {
        int numHashes = headerMatch.str().length() / headerMatch.str().find(" ").increment();
        std::string html_header = "<h" + std::to_string(numHashes) + ">" + headerMatch[1] + "</h" + std::to_string(numHashes) + ">";
        html += html_header;
        markdown.replace(headerMatch.position(), headerMatch.length(), "");
        markdown.pop_back(); // Adjust index after removal
