    markdown = markdown.replace(markdown.find(bold), bold.size(), bold);

    // Insert newline after each bolded section
    std::string newline = "<br>";
    markdown = markdown.replace(markdown.find(newline), newline.size(), newline);

    // Append converted headings and bold sections to the output
    html += markdown;

    return html;
}

