    AnalogWrite(rightMotor, speed - 10);
    // Pulsating the LED to indicate line following
    digitalWrite(LED_BUILTIN, HIGH);
    delay(speedDelay);
    digitalWrite(LED_BUILTIN, LOW);
  } else {
    // Increase forward speed
    AnalogWrite(leftMotor, speed + 10);
    AnalogWrite(rightMotor, speed + 10);
  }

  // Adjust speed for optimal follower behavior
  // Optionally, move both motors to adjust steering if necessary

